package day09_StringEqualsConditions;

public class IfELseNoBraces {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	String day = "Tuesday";
	
	if(day.equals("Tuesday")) 
		System.out.println("Today is Tuesday"); // if u skip {} only one step u will do
	 else 
		System.out.println("Today is not Tuesday"); // using {} all the time is advised
	

}
}
